}

.clear-btn::part(base) {
  border: none;
  width: 100px;
  margin-left: 2vw;
  font-size: 10px;
}

.new-prod-submit-btn {
  margin-top: 0;
}

/*.new-prod-input::part(base) {
  background: var(--sl-color-primary-50);
  max-width: 35vw;
  margin: 0;
}*/
.new-prod-text::part(base) {
  background: var(--sl-color-primary-50);
  border: 3px solid var(--brand-color);
  max-width: 45%;
  min-width: 45%;
  margin-top: 1em;
}

sl-radio {
  margin-bottom: 1em;
}

/*.new-prod-input::part(form-control) {
  max-width: 40vw;
}*/
@media all and (max-width: 820px), (min-width: 768px) {
  sl-dialog::part(panel) {
    width: 100%;
    height: 100%;
