  }
  .filter-btn::part(base) {
    width: 18vw;
    margin-right: 2vw;
    margin-bottom: 1vw;
  }
  .filter-btn::part(label) {
    font-size: 2vw;
  }
  .clear-btn::part(base) {
    width: 16vw;
    margin-top: 2vh;
    margin-left: 0.5vw;
    font-size: 2vw;
  }
  .add-cart-btn::part(base),
.back-btn::part(base) {
    width: 10em;
    height: 2em;
    font-size: 8px;
    padding-bottom: 5em;
  }
}
#toasts {
  position: fixed;
  left: 50%;
  top: 1em;
  transform: translateX(-50%);
  z-index: 999;
  display: flex;
  justify-content: center;
  flex-direction: column;
}

.toast-entry {
  background: #fff;
  margin: 0 auto;
  color: #333;
  border-radius: 200px;
  box-shadow: 0px 3px 25px rgba(0, 0, 0, 0.14);
  padding: 0.7em 1.3em;
  font-size: 1em;
  margin-bottom: 0.25em;
  text-align: center;
  transition: all 0.2 ease;
}
.toast-entry.is-error {
  background: rgb(212, 25, 93);
  color: #fff;
}

sources.scss/_vars.scss:
// Vars

:root {
  // base
  --base-txt-color: #e16a2f;
  --heading-color: #e16a2f;
  --base-font-family: 'sofia-pro', sans-serif;
  --body-bg: #f5dbdf;
  --page-body-bg: #fff;


  // colors  
  $brand-color: #e16a2f;
  --brand-color: #{$brand-color};
  --link-color: --brand-color;

  // app header
  --app-header-height: 10vw;
  --app-header-txt-color: #e16a2f;
  //--app-header-title-font-size: 1.6em;
  //--app-header-border-bottom: 2px solid #fff;
  --body-font-family: 'sofia-pro', sans-serif;

  // shoelace tokens
  // full list here:
  // https://github.com/shoelace-style/shoelace/blob/current/src/styles/base.scss
  --sl-color-primary-50: #f5dbdf;
  --sl-color-primary-100: #{lighten($brand-color, 40%)};
  --sl-color-primary-200: #{lighten($brand-color, 30%)};
  --sl-color-primary-300: #{lighten($brand-color, 20%)};
  --sl-color-primary-400: #{lighten($brand-color, 10%)};
  --sl-color-primary-500: #{$brand-color};
  --sl-color-primary-600: #{darken($brand-color, 10%)};
  --sl-color-primary-700: #{darken($brand-color, 20%)};
  --sl-color-primary-800: #{darken($brand-color, 30%)};
  --sl-color-primary-900: #{darken($brand-color, 40%)};
  --sl-focus-ring-color-primary: #{rgba($brand-color, 0.1)};
}

