// Base

* {
  box-sizing: border-box;
  -webkit-tap-highlight-color: rgba(255, 255, 255, 0);
}

html {
  height: 100%;
}

body {
  padding: 0;
  margin: 0;
  color: var(--base-txt-color);
  font-family: var(--base-font-family);
  font-weight: 300;
  background: var(--page-body-bg);
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.loading {
  img {
    width: 30vw;
    margin: 4vw;
  }
}

.background {
  background-size: 2px;
}

// headings ------------
h1,
h2,
h3,
h5,
h6 {
  margin: 0 0 0.5em;
  color: var(--heading-color);
}

// links ---------------
a {
  color: var(--link-color);
}

a:hover {
  color: white;
}

// homepage ----------------------------------------------------------

.slides {
  //position: absolute;
  //transform: scale(0.9);
  opacity: 0.8;
  transition: all 0.5s ease-in-out;
  //transform: scale(0.9);
