// app splash screen (loading) -------------------------------------------
.app-splash {
  position: fixed;
  background: #fff;
  color: #777;
  display: flex;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  align-items: center;
  justify-content: center;
  text-align: center;

  .app-logo {
    width: 180px;
    display: block;
    margin-bottom: 1em;
  }
}

// Generic setup styling -------------------------------------------------------------------------

// helper classes ---------
.calign {
  text-align: center;
}

.large-text {
  font-size: 1.6em;
}

.brand-color {
  color: var(--brand-color);
}

// generic-entry ----------------
.generic-entry {
  box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.07);
  border-radius: 5px;
  margin-bottom: 0.5em;
  padding: 1em;
  background: #fff;
}

// root --------------------------
#root {
  position: relative;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  overflow: hidden;
}

// page content ------------------

.app-logo-mob {
  width: 200px;
}

.page-content {
  position: relative;
  top: var(--app-header-height);
  left: 0;
  right: 0;
  bottom: var(--app-header-height);
  // overflow-y: scroll;
  padding: 1em;

  &.no-pad {
    padding: 0;
  }

  min-height: 80vh;
  margin-bottom: 10vh;
}

.page-content-post {
  position: relative;
  top: var(--app-header-height);
  left: 0;
  right: 0;
  bottom: 0;
  // overflow-y: scroll;
  padding: 1em;

  &.no-pad {
    padding: 0;
  }

  min-height: 80vh;
  margin-bottom: 10vh;
}

.page-centered {
  display: flex;
  align-items: center;
  justify-content: center;
}

.page-title {
  font-family: var(--body-font-family);
}

// page forms -----------------------------------
.profile,
.editProfile,
.add-product {
  margin-top: 1vw;
  margin-bottom: 2vw;
  padding-bottom: var(--app-header-height);
}

// profile ---------------------------------------
.prof-border {
  margin-top: 20px;
  max-width: 800px;
  padding: 20px;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
  border: 4px solid #fff;
  background-color: rgba(255, 255, 255, 0.3);
  border-radius: 25px;
  height: auto;
}

.prof-txt {
  color: var(--brand-color);
}

.prof-txt-secondary {
  color: #fff;
}



// SIGN IN + SIGN UP ----------------------------------------------------------------------
.sign-in {
  //max-height: 100vh;
  // margin-bottom: var(--app-header-height);
  // position: relative;
  // top: var(--app-header-height);
  left: 0;
  right: 0;
  bottom: 0;
  // overflow-y: scroll;
  padding: 1em;
}

.signinup-box,
.signup-box {
  width: 60vw;
  text-align: center;
  // min-height: max-content;
}

.signinup-box {
  margin-bottom: none !important;
}

.signinup-logo {
  width: 20em;
  margin: 1.2vw 0;
  margin-left: auto;
  margin-right: auto;
  margin-top: 8.5vw;
}

.sign-up-level {
  max-width: 45%;
  min-width: 45%;
  margin: 1vw auto;
}


// HOME ----------------------------------------------------------------------------------------------------
.home-logo {
  width: 12vw;
  margin-top: 1.5vw 0;
  height: auto;
}

.social-media-box {
  margin-top: 10%;
  padding-bottom: 4vw;
}

.link {
