  width: 44vw;
  text-align: left;
  margin: 2vw;
  //margin-left: 50vw;
}

.brandmark-container {
  float: left;
  margin-top: 2vw;
  margin-left: 2vw;
  margin-bottom: 4vw;
}

.about-img {
