.order-summary-column {
  position: absolute;
  margin-top: 1vw;
  right: 0;
  top: 0;
  padding: 2vw 0;
}


// CART POPUP -------------------------------------------------------------------------------------
.wrap {

  img {
    width: 30%;
    position: absolute;
    opacity: 40%;
    margin-left: 32vw;
    margin-top: 5vw;
  }

  .content {
    font-size: 20px;
    margin-left: 25vw;
    margin-top: 10vw;
    text-align: center;
    position: absolute;

  }
}


// ORDERS --------------------------------------------------------------------------------------------------------------

.orders {
  width: 100%;
  height: 100%;
  margin-top: 5vh;
  margin-bottom: 40vh;

  h1 {
    margin-bottom: 5vh;
  }
}

.orders-grid {
  margin-top: 2vw;
}

.orders-table {
  border: 1px solid var(--brand-color);
  border-collapse: collapse;
  width: 90%;
  margin: 0 auto;

  th,
  td {
    padding: 1vw;
    padding-left: 2vw;
    padding-right: 3vw;
  }

  tr {
    border-bottom: 1px solid var(--brand-color);
  }

  th {
    height: 5vh;
    text-align: left;
    text-transform: uppercase;
    font-weight: 400;
    background-color: var(--brand-color);
    color: var(--body-bg);
  }

  td {
    height: 2vh;
    max-width: 5vw;
    text-align: left;
    word-wrap: break-word;
  }
}

tr.customer-order:hover {
  background-color: var(--body-bg);
}

// FAVOURITES ---------------------------------------------------------------------------------------------------------
.favourites {
  p {
    text-align: center;
    font-size: 30px;
  }

  .products-grid {
    margin-bottom: 5vw;
  }

}


//404 ------------------------------------------------------------------------------------------------------------------
.error {
  text-align: center;
  background-color: white;


  .text {
    margin-top: 13vw;

    h1 {
      font-size: 50px;
    }
  }

  img {
    position: absolute;
    width: 21%;
    opacity: 50%;
    margin-top: 10vw;
    margin-left: -10vw;

  }
}

// RESPONSIVE - PRODUCT PAGE min 1024px


@media all and (max-width:1024px) {
  .product-info {
    height: 100vh;
  }
  .about-product {
    width: 45vw;
  }
}


// RESPONSIVE - TABLET ------------
@media all and (max-width:820px) {

  // SIGN IN/UP ------------------------------------------------------------------------------------------------
