// Shoelace

// sl-dialog
sl-dialog::part(title) {
  font-weight: 500;
  font-size: 1.6em;
}

sl-input::part(base) {
  background: var(--sl-color-primary-50);
  border-radius: 20px;
  border: 3px solid var(--brand-color);
  max-width: 45%;
  min-width: 45%;
  position: center;
  margin-bottom: 0.2em;
  margin-left: auto;
  margin-right: auto;
  margin-top: 1em;
}

sl-select::part(suffix) {
  background: var(--sl-color-primary-100);
  border-radius: 15px;
}

sl-textarea::part(base) {
  background: var(--sl-color-primary-100);
  border-radius: 15px;
  margin-bottom: 1em;
}

sl-button::part(base) {
  border-radius: 20px;
  width: 10em;
  border: 3px solid var(--brand-color);
  background: var(--sl-color-primary-50);
  color: var(--brand-color);
}

sl-button::part(base):hover {
  background-color: #e16a2f;
  color: white;
}

.shop-now-btn::part(base) {
  background: var(--brand-color);
  color: var(--page-body-bg);
  width: 12em;
  height: 4em;
  border-radius: 50px;
  padding: 5px;
  margin-top: 0px;
}

.shop-now-btn::part(base):hover {
  background-color: white;
  color: #e16a2f;
}

.product-btn::part(base) {
  background: var(--brand-color);
  color: var(--page-body-bg);
  width: 12em;
  height: 4em;
  border-radius: 50px;
  padding: 5px;
  margin-top: 0px;
}

.add-cart-btn::part(base) {
  background-color: white;
  color: #e16a2f;
  width: 12em;
  height: 4em;
  border-radius: 50px;
  padding: 5px;
  margin-top: 0px;
}

.add-cart-btn::part(base):hover {
  background-color:#e16a2f;
  color: white;
}

.back-btn::part(base) {
  background-color: white;
  color: #e16a2f;
  width: 12em;
  height: 4em;
  border-radius: 50px;
  padding: 5px;
  margin-top: 0px;
}

.back-btn::part(base):hover {
  background-color:#f5dbdf;
  color: white;
}


.filter-btn::part(base) {
  background-color: #fff;
  width: 100px;
  margin-right: 1vw;
  font-size: 10px;
}

.clear-btn::part(base) {
  border: none;
  width: 100px;
  margin-left: 2vw;
  font-size: 10px;
}

.new-prod-submit-btn {
  margin-top: 0;
}

/*.new-prod-input::part(base) {
  background: var(--sl-color-primary-50);
  max-width: 35vw;
  margin: 0;
}*/

.new-prod-text::part(base) {
  background: var(--sl-color-primary-50);
  border: 3px solid var(--brand-color);
  max-width: 45%;
  min-width: 45%;
  margin-top: 1em;
}

sl-radio {
  margin-bottom: 1em;
}

