import { LitElement, html, css } from "@polymer/lit-element";
import { anchorRoute, gotoRoute } from "./../Router";
import Auth from "./../Auth";
//import App from "../App";

customElements.define(
  "cb-app-header",
  class AppHeader extends LitElement {
    constructor() {
      super();
    }

    static get properties() {
      return {
        title: {
          type: String,
        },
        user: {
          type: Object,
        },
      };
    }

    firstUpdated() {
      super.firstUpdated();
      this.navActiveLinks();
    }

    navActiveLinks() {
      const currentPath = window.location.pathname;
      const navLinks = this.shadowRoot.querySelectorAll(
        ".app-top-nav a, .app-side-menu-items a"
      );
      navLinks.forEach((navLink) => {
        if (navLink.href.slice(-1) == "#") return;
        if (navLink.pathname === currentPath) {
          navLink.classList.add("active");
        }
      });
    }

    hamburgerClick() {
      const appMenu = this.shadowRoot.querySelector(".app-side-menu");
      appMenu.show();
    }

    menuClick(e) {
      //e.preventDefault();
      const pathname = e.target.closest("a").pathname;
      const appSideMenu = this.shadowRoot.querySelector(".app-side-menu");
      // hide appMenu
      appSideMenu.hide();
      appSideMenu.addEventListener("sl-after-hide", () => {
        // goto route after menu is hidden
        gotoRoute(pathname);
      });
    }

    render() {
      return html`
        <style>
          * {
            box-sizing: border-box;
          }
          .app-header {
            background: var(--sl-color-primary-50);
            position: fixed;
            top: 0;
            width: 100%;
            height: var(--app-header-height);
            color: var(--app-header-txt-color);
            display: flex;
            z-index: 9;
            align-items: center;
          }

          /* .app-header-main {
            flex-grow: 1;
            display: flex;
            align-items: center;
          } */

          /*.app-header-main::slotted(h1) {
            color: #fff;
          }*/

          /*.app-logo a {
            color: #fff;
            text-decoration: none;
            font-weight: bold;
            font-size: 1.2em;
            padding: 0.6em;
            display: inline-block;
          }*/

          img {
            width: 15vw;
            padding: 0 1.5vw;
            margin: 0 2vw;
          }

          .hamburger-btn::part(base) {
            color: var(--brand-color);
          }

          .app-top-nav {
            display: flex;
            margin: 0 auto;
            height: 100%;
            align-items: center;
          }

          .app-top-nav a {
            display: flex;
            padding: 1vw;
            margin: 0 1.5vw;
            text-decoration: none;
            color: var(--brand-color);

          }

          .app-side-menu-items {
            margin-top: 3em;
          }

          .app-side-menu-items a {
            display: block;
            padding: 0.5em;
            text-decoration: none;
            font-size: 1.3em;
            color: #333;
          }

          .app-side-menu-logo {
            width: 300px;
            margin-bottom: 1em;
            position: absolute;
            top: 2em;
            left: 1.5em;
          }

          sl-icon-button {
            display: none;
          }

          .app-logo-mob {
            display: none;
            width: 150px;
          }

          /* active nav links */
          .app-top-nav a.active,
          .app-side-menu-items a.active {
            font-weight: bold;
            text-decoration: underline;
          }

          /* RESPONSIVE - TABLET ------------------- */
          @media all and (max-width: 768px) {
            .app-top-nav {
              display: none;
            }

            sl-icon-button {
              display: block;
            }

            .app-logo-mob {
              display: block;
              width: 150px;
              margin-left: 270px;

            }

              .app-logo-mob-2 {
                display: block;
                margin: 0 auto;
                width: 240px;

              }

              .app-side-menu-items a {
                display: block;
                padding: 0.6 em;
                margin-left: 1em;
                text-decoration: none;
                font-size: 1.5em;
                color: #e16a2f;
              }
          }

          /* RESPONSIVE - MOBILE ------------------- */
          @media all and (max-width: 425px) {
            .app-top-nav {
              display: none;
            }

            sl-icon-button {
              display: block;
            }

            .app-logo-mob {
              width: 70px;
              margin-left: 110px;
            }
            .app-logo-mob-2 {
              display: block;
              margin: 0 auto;
              width: 180px;
            }

            .app-side-menu-items a {
              display: block;
              padding: 0.4 em;
              text-decoration: none;
              font-size: 1.2em;
              color: #e16a2f;
            }




            }

        </style>

        <header class="app-header">
          <sl-icon-button
            class="hamburger-btn"
            name="list"
            @click="${this.hamburgerClick}"
            style="font-size: 1.5em;"
          ></sl-icon-button>

          <div class="app-header-main">
          <a href="/" @click=${gotoRoute}>
            <img class="app-logo-mob" src="/images/brandmark.png"/>
          </div></a>
          <nav class="app-top-nav">
            <a href="/" @click="${gotoRoute}">HOME</a>
            <a href="/shop" @click="${gotoRoute}">SHOP</a>
              ${
                this.user.accessLevel === undefined
                  ? html``
                  : this.user.accessLevel === 1
                  ? html`
                      <a href="/newProduct" @click="${gotoRoute}"
                        >ADD PRODUCT</a
                      >
                    `
                  : html`<a href="/favouriteProducts" @click="${gotoRoute}"
                      >FAVOURITES</a
                    >`
              }
              <a href="/" @click=${gotoRoute}>
            <img class="app-logo" src="/images/brandmark.png"/></a>
           ${
             this.user.accessLevel === undefined
               ? html``
               : this.user.accessLevel === 1
               ? html` <a href="/orders" @click="${gotoRoute}">ORDERS</a> `
               : html`<a href="/cart" @click="${gotoRoute}">CART</a>`
           }
            <a href="/aboutUs" @click="${gotoRoute}">CONTACT</a>
            <sl-dropdown>
              <a slot="trigger" href="#" @click="${(e) =>
                e.preventDefault()}">ACCOUNT</a>
              <sl-menu>
                 ${
                   this.user.accessLevel === undefined
                     ? html`
                         <sl-menu-item @click="${() => gotoRoute("/signin")}"
                           >Sign In</sl-menu-item
                         >
                         <sl-menu-item @click="${() => gotoRoute("/signup")}"
                           >Sign Up</sl-menu-item
                         >
                       `
                     : html` <sl-menu-item
                           @click="${() => gotoRoute("/profile")}"
                           >Profile</sl-menu-item
                         >
                         <sl-menu-item
                           @click="${() => gotoRoute("/editProfile")}"
                           >Edit Profile</sl-menu-item
                         >
                         <sl-menu-item @click="${() => Auth.signOut()}"
                           >Sign Out</sl-menu-item
                         >`
                 }
              </sl-menu>
             </sl-dropdown>
          </nav>

        </header>

        <sl-drawer class="app-side-menu" placement="left">
          <img class="app-side-menu-logo">
          <nav class="app-side-menu-items">

          <div class="app-side-menu-logo">
          <img class="app-logo-mob-2" src="/images/brandmark.png" />
          </div>
          <a href="/" @click="${gotoRoute}">HOME</a>
            <a href="/shop" @click="${gotoRoute}">SHOP</a>
             ${
               this.user.accessLevel == 1
                 ? html`
                     <a href="/newProduct" @click="${gotoRoute}">ADD PRODUCT</a>
                   `
                 : html`<a href="/favouriteProducts" @click="${gotoRoute}"
                     >FAVOURITES</a
                   >`
             }
            ${
              this.user.accessLevel == 1
                ? html` <a href="/orders" @click="${gotoRoute}">ORDERS</a> `
                : html`<a href="/cart" @click="${gotoRoute}">CART</a>`
            }
            <a href="/aboutUs" @click="${gotoRoute}">CONTACT</a>
            <a href="/profile" @click="${gotoRoute}">MY PROFILE</a>
            <a href="/editProfile" @click="${gotoRoute}">EDIT PROFILE</a>
            <a href="#" @click="${() => Auth.signOut()}">SIGN OUT</a>
          </nav>
        </sl-drawer>
      `;
    }
  }
);
